// Setup basic express server
var express = require("express");
var app = express();
var server = require("http").createServer(app);
var io = require("socket.io")(server);
var port = process.env.PORT || 3000;

server.listen(port, function() {
  console.log("Server listening at port %d", port);
});

// Routing
app.use(express.static("public"));

// DB https://glitch.com/~hello-sqlite
const fs = require("fs");

// init sqlite db
const dbFile = "./.data/sqliteImg.db";
const exists = fs.existsSync(dbFile);
const sqlite3 = require("sqlite3").verbose();
const db = new sqlite3.Database(dbFile);

const ibase =
  "data:image/png;base64,";

// if ./.data/sqlite.db does not exist, create it, otherwise print records to console
db.serialize(() => {
  if (!exists) {
    db.run(
      "CREATE TABLE Images (id INTEGER PRIMARY KEY AUTOINCREMENT, base TEXT)"
    );

    db.serialize(() => {
      for (let i = 0; i < 8; i++) {
        db.run(`INSERT INTO Images (base) VALUES ('${ibase}')`);
      }
    });
  } else {
    db.each("SELECT * from Images", (err, row) => {
      if (row) {
        console.log(`record: ${row.base}`);
      }
    });
  }
});

// Chatroom

var numUsers = 0;
var messageHistory = [];

var imageHistory = [ibase, ibase, ibase, ibase, ibase, ibase, ibase, ibase];
io.on("connection", function(socket) {
  // const base = 'data:image/jpeg;base64,';
  for (let i = imageHistory.length - 8; i < imageHistory.length; i++) {
    socket.emit("new image", { base: imageHistory[i] });
  }
  db.all("SELECT * from Images", (err, rows) => {
    socket.emit("new image debug", { base: JSON.stringify(rows) });
  });

  // when the client emits 'new message', this listens and executes
  socket.on("upload image", function(data) {
    // we tell the client to execute 'new message'
    socket.broadcast.emit("new image", { base: data.base });
    imageHistory.push(data.base);
    imageHistory.shift();

    db.run(`INSERT INTO Images (base) VALUES (?)`, data.base, error => {});
  });

  var addedUser = false;

  // when the client emits 'new message', this listens and executes
  socket.on("new message", function(data) {
    // we tell the client to execute 'new message'
    socket.broadcast.emit("new message", {
      username: socket.username,
      message: data
    });
    messageHistory.push({
      username: socket.username,
      message: data
    });
  });

  // when the client emits 'add user', this listens and executes
  socket.on("add user", function(username) {
    if (addedUser) return;

    // we store the username in the socket session for this client
    socket.username = username;
    ++numUsers;
    addedUser = true;
    socket.emit("login", {
      numUsers: numUsers
    });
    // echo globally (all clients) that a person has connected
    socket.broadcast.emit("user joined", {
      username: socket.username,
      numUsers: numUsers
    });

    for (const message of messageHistory) {
      socket.emit("new message", message);
    }
  });

  // when the client emits 'typing', we broadcast it to others
  socket.on("typing", function() {
    socket.broadcast.emit("typing", {
      username: socket.username
    });
  });

  // when the client emits 'stop typing', we broadcast it to others
  socket.on("stop typing", function() {
    socket.broadcast.emit("stop typing", {
      username: socket.username
    });
  });

  // when the user disconnects.. perform this
  socket.on("disconnect", function() {
    if (addedUser) {
      --numUsers;

      // echo globally that this client has left
      socket.broadcast.emit("user left", {
        username: socket.username,
        numUsers: numUsers
      });
    }
  });
});
